import { Message } from "@material-ui/icons";
function App() {

 const {user, isFecthing,error, dispatch} = useContext(AuthContext)

  console.log(user)
  return (



    <Router>
      <Switch>

            <Route exact path="/">
          {
         user ?  <Home /> : <Register/>
          }

        </Route>

        <Route path="/login">
          {
            user? <Redirect to='/'/> :  <Login />
          }

        </Route>
