            user? <Redirect to='/'/> :  <Message/>
          }

        </Route>
        <Route path="/register">
          {
            user? <Redirect to='/'/> :  <Register /> 
          }

        </Route>
        <Route path="/profile/:username">
          <Profile />
        </Route>{" "}

      </Switch>
    </Router>


  );
}

export default App;
