            </div>
            <div className="chat_box_bottom">


            <textarea
              placeholder="write some.."
              className="chatmsginput"
            ></textarea>

            <button className="chat_button">send</button>
            </div>
          </div>
        </div>
        <div className="chatonline">
          <div className="chatonline__wraaper">
<Chatonline/>

          </div>
        </div>
      </div>
    </div>
  );
}

export default Messenge;
